const open=document.querySelector(".contacts_button"),popup=document.querySelector(".modal"),close=document.querySelector(".modal_close_button"),form=popup.querySelector(".modal_form"),username=popup.querySelector("[name=username]");open.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal_show"),username.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal_show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal_show")&&(e.preventDefault(),popup.classList.remove("modal_show"))});const slideToggleBtns=document.querySelectorAll(".slide_control"),slides=document.querySelectorAll(".features_item"),clearSelected=function(){document.querySelector(".slide_control--current").classList.remove("slide_control--current"),document.querySelector(".features_item--current").classList.remove("features_item--current")};slideToggleBtns.forEach(function(e,t){e.addEventListener("click",function(e){clearSelected(),e.currentTarget.classList.add("slide_control--current"),slides[t].classList.add("features_item--current")})});
